
public class Chemical {
	
	String name;
	int price;
	
	public Chemical() {
		// TODO Auto-generated constructor stub
	}

	public Chemical(String name, int price) {
		super();
		this.name = name;
		this.price = price;
	}
	
	
	public void print() {
		System.out.println(name);
		System.out.println(price);
	}
	
	

}
